mapper.select:
SELECT 

FROM weixin_user a

WHERE a.id = #{id}

SELECT
FROM weixin_user a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.company LIKE '%'||#{company}||'%' '%'+#{company}+'%' concat('%',#{company},'%')
AND a.register_phone = #{registerPhone}
AND a.type = #{type}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM weixin_user a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE weixin_user SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
